/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
/*                                                                  */
/* Hessian - function for Hessian (Jac derivative w.r.t. variables) */
/*   Arguments :                                                    */
/*      V         - Concentrations of variable species (local)      */
/*      F         - Concentrations of fixed species (local)         */
/*      RCT       - Rate constants (local)                          */
/*      HESS      - Hessian of Var (i.e. the 3-tensor d Jac / d Var) */
/*                                                                  */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */

void Hessian( 
  double V[],                            /* Concentrations of variable species (local) */
  double F[],                            /* Concentrations of fixed species (local) */
  double RCT[],                          /* Rate constants (local) */
  double HESS[]                          /* Hessian of Var (i.e. the 3-tensor d Jac / d Var) */
)
{
/* --------------------------------------------------------         */
/* Note: HESS is represented in coordinate sparse format:           */
/*       HESS(m) = d^2 f_i / dv_j dv_k = d Jac_{i,j} / dv_k         */
/*       where i = IHESS_I(m), j = IHESS_J(m), k = IHESS_K(m).      */
/* --------------------------------------------------------         */
/* Note: d^2 f_i / dv_j dv_k = d^2 f_i / dv_k dv_j,                 */
/*       therefore only the terms d^2 f_i / dv_j dv_k               */
/*       with j <= k are computed and stored in HESS.               */
/* --------------------------------------------------------         */

/* Local variables                                                  */
double D2A[8];                           /* Second derivatives of equation rates */

/* Computation of the second derivatives of equation rates          */
/* D2A(0) = d^2 A(1) / dV(7)dV(8)                                   */
  D2A[0] = RCT[1];
/* D2A(1) = d^2 A(3)/{dV(7)dV(7)}                                   */
  D2A[1] = RCT[3]*2;
/* D2A(2) = d^2 A(6) / dV(8)dV(10)                                  */
  D2A[2] = RCT[6];
/* D2A(3) = d^2 A(7) / dV(7)dV(10)                                  */
  D2A[3] = RCT[7];
/* D2A(4) = d^2 A(10) / dV(9)dV(10)                                 */
  D2A[4] = RCT[10];
/* D2A(5) = d^2 A(11) / dV(7)dV(9)                                  */
  D2A[5] = RCT[11];
/* D2A(6) = d^2 A(12) / dV(7)dV(9)                                  */
  D2A[6] = RCT[12];
/* D2A(7) = d^2 A(16) / dV(3)dV(8)                                  */
  D2A[7] = 8e-17;

/* Computation of the Jacobian derivative                           */
/* HESS(0) = d^2 Vdot(1)/{dV(7)dV(7)} = d^2 Vdot(1)/{dV(7)dV(7)}    */
  HESS[0] = D2A[1];
/* HESS(1) = d^2 Vdot(3)/{dV(3)dV(8)} = d^2 Vdot(3)/{dV(8)dV(3)}    */
  HESS[1] = -D2A[7];
/* HESS(2) = d^2 Vdot(4)/{dV(7)dV(9)} = d^2 Vdot(4)/{dV(9)dV(7)}    */
  HESS[2] = D2A[6];
/* HESS(3) = d^2 Vdot(4)/{dV(9)dV(10)} = d^2 Vdot(4)/{dV(10)dV(9)}  */
  HESS[3] = D2A[4];
/* HESS(4) = d^2 Vdot(5)/{dV(7)dV(9)} = d^2 Vdot(5)/{dV(9)dV(7)}    */
  HESS[4] = D2A[5];
/* HESS(5) = d^2 Vdot(6)/{dV(7)dV(10)} = d^2 Vdot(6)/{dV(10)dV(7)}  */
  HESS[5] = D2A[3];
/* HESS(6) = d^2 Vdot(6)/{dV(8)dV(10)} = d^2 Vdot(6)/{dV(10)dV(8)}  */
  HESS[6] = D2A[2];
/* HESS(7) = d^2 Vdot(6)/{dV(9)dV(10)} = d^2 Vdot(6)/{dV(10)dV(9)}  */
  HESS[7] = D2A[4];
/* HESS(8) = d^2 Vdot(7)/{dV(7)dV(7)} = d^2 Vdot(7)/{dV(7)dV(7)}    */
  HESS[8] = -2*D2A[1];
/* HESS(9) = d^2 Vdot(7)/{dV(7)dV(8)} = d^2 Vdot(7)/{dV(8)dV(7)}    */
  HESS[9] = -D2A[0];
/* HESS(10) = d^2 Vdot(7)/{dV(7)dV(9)} = d^2 Vdot(7)/{dV(9)dV(7)}   */
  HESS[10] = -D2A[5]-D2A[6];
/* HESS(11) = d^2 Vdot(7)/{dV(7)dV(10)} = d^2 Vdot(7)/{dV(10)dV(7)} */
  HESS[11] = -D2A[3];
/* HESS(12) = d^2 Vdot(7)/{dV(9)dV(10)} = d^2 Vdot(7)/{dV(10)dV(9)} */
  HESS[12] = D2A[4];
/* HESS(13) = d^2 Vdot(8)/{dV(3)dV(8)} = d^2 Vdot(8)/{dV(8)dV(3)}   */
  HESS[13] = -D2A[7];
/* HESS(14) = d^2 Vdot(8)/{dV(7)dV(8)} = d^2 Vdot(8)/{dV(8)dV(7)}   */
  HESS[14] = -D2A[0];
/* HESS(15) = d^2 Vdot(8)/{dV(8)dV(10)} = d^2 Vdot(8)/{dV(10)dV(8)} */
  HESS[15] = -D2A[2];
/* HESS(16) = d^2 Vdot(9)/{dV(3)dV(8)} = d^2 Vdot(9)/{dV(8)dV(3)}   */
  HESS[16] = 0.75*D2A[7];
/* HESS(17) = d^2 Vdot(9)/{dV(7)dV(9)} = d^2 Vdot(9)/{dV(9)dV(7)}   */
  HESS[17] = -D2A[5]-D2A[6];
/* HESS(18) = d^2 Vdot(9)/{dV(9)dV(10)} = d^2 Vdot(9)/{dV(10)dV(9)} */
  HESS[18] = -D2A[4];
/* HESS(19) = d^2 Vdot(10)/{dV(7)dV(10)} = d^2 Vdot(10)/{dV(10)dV(7)} */
  HESS[19] = -D2A[3];
/* HESS(20) = d^2 Vdot(10)/{dV(8)dV(10)} = d^2 Vdot(10)/{dV(10)dV(8)} */
  HESS[20] = -D2A[2];
/* HESS(21) = d^2 Vdot(10)/{dV(9)dV(10)} = d^2 Vdot(10)/{dV(10)dV(9)} */
  HESS[21] = -D2A[4];
}

/* End of Hessian function                                          */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */


/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
/*                                                                  */
/* HessTR_Vec - Hessian transposed times user vectors               */
/*   Arguments :                                                    */
/*      HESS      - Hessian of Var (i.e. the 3-tensor d Jac / d Var) */
/*      U1        - User vector                                     */
/*      U2        - User vector                                     */
/*      HTU       - Transposed Hessian times user vectors: (Hess x U2)^T * U1 = [d (Jac^T*U1)/d Var] * U2  */
/*                                                                  */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */

void HessTR_Vec( 
  double HESS[],                         /* Hessian of Var (i.e. the 3-tensor d Jac / d Var) */
  double U1[],                           /* User vector */
  double U2[],                           /* User vector */
  double HTU[]                           /* Transposed Hessian times user vectors: (Hess x U2)^T * U1 = [d (Jac^T*U1)/d Var] * U2  */
)
{
/* Compute the vector HTU =(Hess x U2)^T * U1 = d (Jac^T*U1)/d Var * U2  */
  HTU[0] = 0;
  HTU[1] = 0;
  HTU[2] = 0;
  HTU[3] = HESS[1]*(U1[3]*U2[8])+HESS[13]*(U1[8]*U2[8])+HESS[16]*(U1[9]
          *U2[8]);
  HTU[4] = 0;
  HTU[5] = 0;
  HTU[6] = 0;
  HTU[7] = HESS[0]*(U1[1]*U2[7])+HESS[2]*(U1[4]*U2[9])+HESS[4]*(U1[5]
          *U2[9])+HESS[5]*(U1[6]*U2[10])+HESS[8]*(U1[7]*U2[7])+HESS[9]
          *(U1[7]*U2[8])+HESS[10]*(U1[7]*U2[9])+HESS[11]*(U1[7]*U2[10])
          +HESS[14]*(U1[8]*U2[8])+HESS[17]*(U1[9]*U2[9])+HESS[19]
          *(U1[10]*U2[10]);
  HTU[8] = HESS[1]*(U1[3]*U2[3])+HESS[6]*(U1[6]*U2[10])+HESS[9]*(U1[7]
          *U2[7])+HESS[13]*(U1[8]*U2[3])+HESS[14]*(U1[8]*U2[7])
          +HESS[15]*(U1[8]*U2[10])+HESS[16]*(U1[9]*U2[3])+HESS[20]
          *(U1[10]*U2[10]);
  HTU[9] = HESS[2]*(U1[4]*U2[7])+HESS[3]*(U1[4]*U2[10])+HESS[4]*(U1[5]
          *U2[7])+HESS[7]*(U1[6]*U2[10])+HESS[10]*(U1[7]*U2[7])
          +HESS[12]*(U1[7]*U2[10])+HESS[17]*(U1[9]*U2[7])+HESS[18]
          *(U1[9]*U2[10])+HESS[21]*(U1[10]*U2[10]);
  HTU[10] = HESS[3]*(U1[4]*U2[9])+HESS[5]*(U1[6]*U2[7])+HESS[6]*(U1[6]
           *U2[8])+HESS[7]*(U1[6]*U2[9])+HESS[11]*(U1[7]*U2[7])
           +HESS[12]*(U1[7]*U2[9])+HESS[15]*(U1[8]*U2[8])+HESS[18]
           *(U1[9]*U2[9])+HESS[19]*(U1[10]*U2[7])+HESS[20]*(U1[10]
           *U2[8])+HESS[21]*(U1[10]*U2[9]);
}

/* End of HessTR_Vec function                                       */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */


/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
/*                                                                  */
/* Hess_Vec - Hessian times user vectors                            */
/*   Arguments :                                                    */
/*      HESS      - Hessian of Var (i.e. the 3-tensor d Jac / d Var) */
/*      U1        - User vector                                     */
/*      U2        - User vector                                     */
/*      HU        - Hessian times user vectors: (Hess x U2) * U1 = [d (Jac*U1)/d Var] * U2 */
/*                                                                  */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */

void Hess_Vec( 
  double HESS[],                         /* Hessian of Var (i.e. the 3-tensor d Jac / d Var) */
  double U1[],                           /* User vector */
  double U2[],                           /* User vector */
  double HU[]                            /* Hessian times user vectors: (Hess x U2) * U1 = [d (Jac*U1)/d Var] * U2 */
)
{
/* Compute the vector HU =(Hess x U2) * U1 = d (Jac*U1)/d Var * U2  */
  HU[0] = 0;
  HU[1] = HESS[0]*(U1[7]*U2[7]);
  HU[2] = 0;
  HU[3] = HESS[1]*(U1[3]*U2[8])+HESS[1]*(U1[8]*U2[3]);
  HU[4] = HESS[2]*(U1[7]*U2[9])+HESS[2]*(U1[9]*U2[7])+HESS[3]*(U1[9]
         *U2[10])+HESS[3]*(U1[10]*U2[9]);
  HU[5] = HESS[4]*(U1[7]*U2[9])+HESS[4]*(U1[9]*U2[7]);
  HU[6] = HESS[5]*(U1[7]*U2[10])+HESS[5]*(U1[10]*U2[7])+HESS[6]*(U1[8]
         *U2[10])+HESS[6]*(U1[10]*U2[8])+HESS[7]*(U1[9]*U2[10])+HESS[7]
         *(U1[10]*U2[9]);
  HU[7] = HESS[8]*(U1[7]*U2[7])+HESS[9]*(U1[7]*U2[8])+HESS[9]*(U1[8]
         *U2[7])+HESS[10]*(U1[7]*U2[9])+HESS[10]*(U1[9]*U2[7])+HESS[11]
         *(U1[7]*U2[10])+HESS[11]*(U1[10]*U2[7])+HESS[12]*(U1[9]
         *U2[10])+HESS[12]*(U1[10]*U2[9]);
  HU[8] = HESS[13]*(U1[3]*U2[8])+HESS[13]*(U1[8]*U2[3])+HESS[14]*(U1[7]
         *U2[8])+HESS[14]*(U1[8]*U2[7])+HESS[15]*(U1[8]*U2[10])
         +HESS[15]*(U1[10]*U2[8]);
  HU[9] = HESS[16]*(U1[3]*U2[8])+HESS[16]*(U1[8]*U2[3])+HESS[17]*(U1[7]
         *U2[9])+HESS[17]*(U1[9]*U2[7])+HESS[18]*(U1[9]*U2[10])
         +HESS[18]*(U1[10]*U2[9]);
  HU[10] = HESS[19]*(U1[7]*U2[10])+HESS[19]*(U1[10]*U2[7])+HESS[20]
          *(U1[8]*U2[10])+HESS[20]*(U1[10]*U2[8])+HESS[21]*(U1[9]
          *U2[10])+HESS[21]*(U1[10]*U2[9]);
}

/* End of Hess_Vec function                                         */
